import axios from 'axios';
import { Modal } from 'bootstrap';
import Swal from 'sweetalert2';
import moment from 'moment';
import { mapGetters, mapActions } from 'vuex';
export default {
  name: 'Solicitudes',
  computed: {
    ...mapGetters(['tiposSolicitudes', 'isLoading']),
    // Usar datos de Vuex como fuente principal - CON VALIDACIÓN
    solicitudes() {
      // VALIDACIÓN CRÍTICA: Asegurarse de que siempre devuelva un array
      if (this.tiposSolicitudes && Array.isArray(this.tiposSolicitudes) && this.tiposSolicitudes.length > 0) {
        return this.tiposSolicitudes;
      }
      // Fallback a datos locales
      return Array.isArray(this.localSolicitudes) ? this.localSolicitudes : [];
    },
    // Estado de loading desde Vuex
    isLoadingSolicitudes() {
      if (typeof this.isLoading === 'function') {
        return this.isLoading('tiposSolicitudes');
      }
      return false;
    }
  },
  data() {
    return {
      // Fallback local para compatibilidad - INICIALIZADO COMO ARRAY
      localSolicitudes: [],
      showModal: false,
      selectedSolicitud: null,
      selectedSolicitudTitle: '',
      solicitud_permiso: {
        motivo: '',
        observacion: '',
        fecha_inicio: '',
        fecha_fin: ''
      },
      solicitud_reincorporacion: {
        motivo: '',
        observacion: '',
        fecha_inicio: ''
      },
      solicitud_uso_tiempo: {
        duracion: '',
        unidad: 'horas',
        observacion: '',
        fecha_inicio: '',
        fecha_fin: ''
      },
      solicitud_vacaciones: {
        fecha_inicio: '',
        salario: '',
        dias: '',
        fecha_fin: ''
      },
      solicitud_horas_extras: {
        fecha_inicio: '',
        fecha_fin: '',
        trabajo_realizado: '',
        justificacion: '',
        observacion: ''
      },
      motivosDescontables: ['Enfermedad', 'Duelo', 'Matrimonio', 'Nacimiento de hijos', 'Enfermedades de parientes cercanos', 'Eventos academicos puntuales', 'Permisos personales'],
      motivosNoDescontables: ['Mision oficial', 'Seminarios', 'Otros, especifique'],
      motivosReincorporacion: ['Licencia con sueldo', 'Licencia sin sueldo', 'Licencia especial', 'Vacaciones'],
      solicitudModalInstance: null
    };
  },
  async created() {
    try {
      // VALIDACIÓN: Solo llamar a Vuex si existe
      if (this.$store && this.fetchTiposSolicitudes) {
        // Si no hay datos en Vuex, intentar cargarlos
        if (!this.tiposSolicitudes || this.tiposSolicitudes.length === 0) {
          await this.fetchTiposSolicitudes();
        }
      } else {
        // Fallback: cargar datos directamente
        await this.fetchSolicitudes();
      }
    } catch (error) {
      console.error('Error en created hook:', error);
      // Fallback final
      await this.fetchSolicitudes();
    }

    // Inicializar modal
    this.$nextTick(() => {
      try {
        const modalElement = document.getElementById('solicitudModal');
        if (modalElement) {
          this.solicitudModalInstance = new Modal(modalElement);
        }
      } catch (error) {
        console.error('Error inicializando modal:', error);
      }
    });
  },
  methods: {
    ...mapActions(['fetchTiposSolicitudes']),
    // Fallback method - SIEMPRE disponible
    async fetchSolicitudes() {
      try {
        const response = await axios.get('http://localhost:8000/api/listar_solicitud');
        this.localSolicitudes = Array.isArray(response.data) ? response.data : [];
      } catch (error) {
        console.error('Error fetching solicitudes', error);
        this.localSolicitudes = []; // Asegurar que siempre sea un array
      }
    },
    handleCardClick(id) {
      this.selectedSolicitud = id;
      this.selectedSolicitudTitle = this.getSolicitudTitle(id);
      this.showModal = true;
      if (this.solicitudModalInstance) {
        this.solicitudModalInstance.show();
      }
    },
    closeModal() {
      this.showModal = false;
      if (this.solicitudModalInstance) {
        this.solicitudModalInstance.hide();
      }
    },
    getSolicitudTitle(id) {
      // VALIDACIÓN: Asegurar que solicitudes sea un array
      if (!Array.isArray(this.solicitudes)) {
        return '';
      }
      const solicitud = this.solicitudes.find(s => s && s.id_tipo_solicitud === id);
      return solicitud ? solicitud.tipo_solicitud : '';
    },
    validarFechas(fecha_inicio, fecha_fin) {
      if (new Date(fecha_fin) < new Date(fecha_inicio)) {
        Swal.fire({
          icon: 'error',
          title: 'Error en las fechas',
          text: 'La fecha de fin no puede ser antes de la fecha de inicio.',
          timer: 2000,
          showConfirmButton: false
        });
        return false;
      }
      return true;
    },
    validarPermiso(fecha_inicio, fecha_fin) {
      const inicio = moment(fecha_inicio, 'YYYY-DD-MM HH:mm');
      const fin = moment(fecha_fin, 'YYYY-DD-MM HH:mm');
      const diasSemana = [1, 2, 3, 4, 5]; // Lunes a viernes
      const horaInicioPermitida = moment('08:30', 'HH:mm');
      const horaFinPermitida = moment('16:00', 'HH:mm');

      // Validar que las fechas no sean fines de semana
      if (!diasSemana.includes(inicio.day()) || !diasSemana.includes(fin.day())) {
        console.log(diasSemana.includes(inicio.day()));
        Swal.fire({
          icon: 'error',
          title: 'Fecha no válida',
          text: 'No se puede seleccionar sábado o domingo.',
          timer: 2000,
          showConfirmButton: false
        });
        return false;
      }

      // Validar que las horas estén entre 08:30 y 16:00
      const horaInicio = moment(inicio.format('HH:mm'), 'HH:mm');
      const horaFin = moment(fin.format('HH:mm'), 'HH:mm');
      if (horaInicio.isBefore(horaInicioPermitida) || horaInicio.isAfter(horaFinPermitida) || horaFin.isBefore(horaInicioPermitida) || horaFin.isAfter(horaFinPermitida)) {
        Swal.fire({
          icon: 'error',
          title: 'Hora no válida',
          text: 'La hora debe estar entre las 08:30 y las 16:00.',
          timer: 2000,
          showConfirmButton: false
        });
        return false;
      }
      return true;
    },
    validarTiempoCompensatorio(fecha_inicio, fecha_fin, duracion, unidad) {
      const inicio = moment(fecha_inicio, 'YYYY-DD-MM HH:mm');
      const fin = moment(fecha_fin, 'YYYY-DD-MM HH:mm');
      if (unidad === 'horas') {
        const diffHoras = fin.diff(inicio, 'hours');
        if (diffHoras !== duracion) {
          Swal.fire({
            icon: 'error',
            title: 'Error en la duración',
            text: `La duración debe ser exactamente ${duracion} horas.`,
            timer: 2000,
            showConfirmButton: false
          });
          return false;
        }
      } else if (unidad === 'días') {
        const diffDias = fin.diff(inicio, 'days');
        if (diffDias !== duracion) {
          Swal.fire({
            icon: 'error',
            title: 'Error en la duración',
            text: `La duración debe ser exactamente ${duracion} días.`,
            timer: 2000,
            showConfirmButton: false
          });
          return false;
        }
      }
      return true;
    },
    formatFecha(fecha) {
      return moment(fecha).format('YYYY-DD-MM HH:mm');
    },
    formatFechaVacaciones(fecha) {
      return moment(fecha).format('YYYY-DD-MM 00:00');
    },
    validarHorario(fecha_inicio, fecha_fin) {
      const hora_inicio = moment(fecha_inicio, 'YYYY-DD-MM HH:mm').format('HH:mm');
      const hora_fin = moment(fecha_fin, 'YYYY-DD-MM HH:mm').format('HH:mm');
      if (hora_inicio === hora_fin) {
        Swal.fire({
          icon: 'warning',
          title: 'Advertencia',
          text: 'La hora no puede ser la misma.',
          timer: 2000,
          showConfirmButton: false
        });
        return false;
      }
      return true;
    },
    validarVacaciones(fecha_inicio, fecha_fin, duracion) {
      const inicio = moment(fecha_inicio, 'YYYY-DD-MM HH:mm');
      const fin = moment(fecha_fin, 'YYYY-DD-MM HH:mm');

      // Validar que las fechas coincidan con la duración
      const diffDias = fin.diff(inicio, 'days'); // +1 para incluir el día de inicio
      if (diffDias !== duracion) {
        console.log(diffDias, duracion);
        Swal.fire({
          icon: 'error',
          title: 'Duración no válida',
          text: `La duración debe ser exactamente ${duracion} días.`,
          timer: 2000,
          showConfirmButton: false
        });
        return false;
      }

      // Validar que la duración sea 15 o 30 días
      if (duracion !== 15 && duracion !== 30) {
        Swal.fire({
          icon: 'error',
          title: 'Duración no válida',
          text: 'La duración de las vacaciones debe ser de 15 o 30 días.',
          timer: 2000,
          showConfirmButton: false
        });
        return false;
      }
      return true;
    },
    validarHorasExtras(fecha_inicio, fecha_fin) {
      const inicio = moment(fecha_inicio, 'YYYY-DD-MM HH:mm');
      const fin = moment(fecha_fin, 'YYYY-DD-MM HH:mm');

      // Verificar si las fechas son el mismo día
      if (!inicio.isSame(fin, 'day')) {
        Swal.fire({
          icon: 'warning',
          title: 'Advertencia',
          text: 'Las horas extras deben ser solicitadas para el mismo día.',
          timer: 3000,
          showConfirmButton: false
        });
        return false;
      }

      // Obtener el día de la semana
      const diaSemana = inicio.day();

      // Si el día es sábado (6), no aplicar la validación del horario laboral
      if (diaSemana === 6) {
        return true;
      }

      // Definir horarios laborales permitidos
      const horaInicioLaboral = moment('08:30', 'HH:mm');
      const horaFinLaboral = moment('15:59', 'HH:mm');

      // Obtener las horas y minutos de las fechas de inicio y fin
      const horaInicio = moment(inicio.format('HH:mm'), 'HH:mm');
      const horaFin = moment(fin.format('HH:mm'), 'HH:mm');

      // Verificar si las horas están dentro del horario laboral
      if (horaInicio.isBetween(horaInicioLaboral, horaFinLaboral, null, '[]') || horaFin.isBetween(horaInicioLaboral, horaFinLaboral, null, '[]')) {
        Swal.fire({
          icon: 'warning',
          title: 'Advertencia',
          text: 'No se puede solicitar horas extras dentro de horario laboral.',
          timer: 3000,
          showConfirmButton: false
        });
        return false;
      }
      return true;
    },
    async enviarSolicitud() {
      try {
        let solicitudData;
        if (this.selectedSolicitud === 1) {
          // Formatear fechas al formato yyyy-dd-mm HH:mm
          const fecha_inicio = this.formatFecha(this.solicitud_permiso.fecha_inicio);
          const fecha_fin = this.formatFecha(this.solicitud_permiso.fecha_fin);

          // Validar fechas
          if (!this.validarFechas(fecha_inicio, fecha_fin)) {
            return;
          }
          if (!this.validarPermiso(fecha_inicio, fecha_fin)) {
            return;
          }
          solicitudData = {
            motivo: this.solicitud_permiso.motivo,
            observacion: this.solicitud_permiso.observacion,
            fecha_inicio: fecha_inicio,
            fecha_fin: fecha_fin,
            tipo_solicitud: this.selectedSolicitud
          };
        } else if (this.selectedSolicitud === 2) {
          const fecha_inicio = this.formatFecha(this.solicitud_reincorporacion.fecha_inicio);
          solicitudData = {
            motivo: this.solicitud_reincorporacion.motivo,
            observacion: this.solicitud_reincorporacion.observacion,
            fecha_inicio: fecha_inicio,
            tipo_solicitud: this.selectedSolicitud
          };
        } else if (this.selectedSolicitud === 3) {
          // Formatear fechas al formato yyyy-dd-mm HH:mm
          const fecha_inicio = this.formatFecha(this.solicitud_uso_tiempo.fecha_inicio);
          const fecha_fin = this.formatFecha(this.solicitud_uso_tiempo.fecha_fin);

          // Validar tiempo compensatorio
          if (!this.validarTiempoCompensatorio(fecha_inicio, fecha_fin, this.solicitud_uso_tiempo.duracion, this.solicitud_uso_tiempo.unidad)) {
            return;
          }
          if (!this.validarPermiso(fecha_inicio, fecha_fin)) {
            return;
          }
          solicitudData = {
            observacion: this.solicitud_uso_tiempo.observacion,
            fecha_inicio: fecha_inicio,
            fecha_fin: fecha_fin,
            tiempo_utilizado: `${this.solicitud_uso_tiempo.duracion} ${this.solicitud_uso_tiempo.unidad}`,
            tipo_solicitud: this.selectedSolicitud
          };
        } else if (this.selectedSolicitud === 4) {
          const fecha_inicio = this.formatFechaVacaciones(this.solicitud_vacaciones.fecha_inicio);
          const fecha_fin = this.formatFechaVacaciones(this.solicitud_vacaciones.fecha_fin);

          // Validar fechas
          if (!this.validarFechas(fecha_inicio, fecha_fin)) {
            return;
          }
          if (!this.validarVacaciones(fecha_inicio, fecha_fin, this.solicitud_vacaciones.dias)) {
            return;
          }
          solicitudData = {
            fecha_inicio: fecha_inicio,
            salario: this.solicitud_vacaciones.salario,
            dias: this.solicitud_vacaciones.dias,
            fecha_fin: fecha_fin,
            tipo_solicitud: this.selectedSolicitud
          };
        } else if (this.selectedSolicitud === 5) {
          const fecha_inicio = this.formatFecha(this.solicitud_horas_extras.fecha_inicio);
          const fecha_fin = this.formatFecha(this.solicitud_horas_extras.fecha_fin);

          // Validar fechas
          if (!this.validarFechas(fecha_inicio, fecha_fin)) {
            return;
          }
          if (!this.validarHorasExtras(fecha_inicio, fecha_fin)) {
            return;
          }
          if (!this.validarHorario(fecha_inicio, fecha_fin)) {
            return;
          }
          solicitudData = {
            fecha_inicio: fecha_inicio,
            fecha_fin: fecha_fin,
            trabajo_realizado: this.solicitud_horas_extras.trabajo_realizado,
            justificacion: this.solicitud_horas_extras.justificacion,
            observacion: this.solicitud_horas_extras.observacion,
            tipo_solicitud: this.selectedSolicitud
          };
        }
        const response = await axios.post('http://localhost:8000/api/insertar_solicitud', solicitudData);
        if (response.data.status) {
          Swal.fire({
            icon: 'success',
            title: 'Éxito',
            text: response.data.message,
            timer: 2000,
            showConfirmButton: false
          }).then(() => {
            this.closeModal();
            setTimeout(() => {
              location.reload();
            }, 700);
          });
        }
      } catch (error) {
        console.error('Error al enviar la solicitud', error);
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: 'Hubo un problema al enviar la solicitud'
        });
      }
    }
  }
};
