import axios from 'axios';
import moment from 'moment';
import 'moment/locale/es';
import { Modal } from 'bootstrap';
import Swal from 'sweetalert2';
import { mapGetters, mapActions } from 'vuex';
export default {
  name: 'HistorialSolicitudes',
  computed: {
    ...mapGetters(['historialSolicitudes', 'tiposSolicitudes', 'isLoading']),
    // Usar datos de Vuex como fuente principal - CON VALIDACIONES
    solicitudes() {
      // VALIDACIÓN: Asegurar que siempre devuelva un array
      if (this.historialSolicitudes && Array.isArray(this.historialSolicitudes) && this.historialSolicitudes.length > 0) {
        return this.historialSolicitudes;
      }
      // Fallback a datos locales
      return Array.isArray(this.localSolicitudes) ? this.localSolicitudes : [];
    },
    tiposSolicitudesData() {
      // RENOMBRADO para evitar conflicto
      // VALIDACIÓN: Asegurar que siempre devuelva un array
      if (this.tiposSolicitudes && Array.isArray(this.tiposSolicitudes) && this.tiposSolicitudes.length > 0) {
        return this.tiposSolicitudes;
      }
      // Fallback a datos locales
      return Array.isArray(this.localTiposSolicitudes) ? this.localTiposSolicitudes : [];
    },
    // Estado de loading desde Vuex - RENOMBRADO para evitar conflicto
    isLoadingData() {
      if (typeof this.isLoading === 'function') {
        return this.isLoading('historialSolicitudes');
      }
      return false;
    },
    // Computed para filtros
    solicitudesFiltradas() {
      // VALIDACIÓN: Asegurar que solicitudes sea un array
      if (!Array.isArray(this.solicitudes)) {
        return [];
      }
      return this.solicitudes.filter(solicitud => {
        if (!solicitud || !solicitud.tipo_solicitud) return false;
        const cumpleTipo = this.filtroTipo ? solicitud.tipo_solicitud.id_tipo_solicitud === parseInt(this.filtroTipo) : true;
        const cumpleFechaInicio = this.filtroFechaInicio ? new Date(solicitud.fecha_creacion) >= new Date(this.filtroFechaInicio) : true;
        const cumpleFechaFin = this.filtroFechaFin ? new Date(solicitud.fecha_creacion) <= new Date(this.filtroFechaFin) : true;
        return cumpleTipo && cumpleFechaInicio && cumpleFechaFin;
      });
    },
    // Computed para formularios
    esFormularioPermiso() {
      return this.solicitudEditando && this.solicitudEditando.tipo_solicitud_id === 1;
    },
    esFormularioReincorporacion() {
      return this.solicitudEditando && this.solicitudEditando.tipo_solicitud_id === 2;
    },
    esFormularioVacaciones() {
      return this.solicitudEditando && this.solicitudEditando.tipo_solicitud_id === 4;
    },
    esFormularioHorasExtras() {
      return this.solicitudEditando && this.solicitudEditando.tipo_solicitud_id === 5;
    }
  },
  data() {
    return {
      // Fallback local para compatibilidad - INICIALIZADOS COMO ARRAYS
      localSolicitudes: [],
      localTiposSolicitudes: [],
      // Filtros
      filtroTipo: '',
      filtroFechaInicio: '',
      filtroFechaFin: '',
      // Estados de carga - REMOVIDO isLoading para evitar conflicto
      isUpdating: false,
      isGeneratingPDF: null,
      // Modal de edición
      solicitudEditando: null,
      editModalInstance: null,
      editErrors: {},
      // Modal de PDF
      pdfModalInstance: null,
      solicitudViendoPDF: null,
      pdfUrl: null,
      // Motivos predefinidos
      motivosDescontables: [{
        valor: 'Enfermedad',
        nombre: 'Enfermedad'
      }, {
        valor: 'Duelo',
        nombre: 'Duelo'
      }, {
        valor: 'Matrimonio',
        nombre: 'Matrimonio'
      }, {
        valor: 'Nacimiento de hijos',
        nombre: 'Nacimiento de hijos'
      }, {
        valor: 'Enfermedades de parientes cercanos',
        nombre: 'Enfermedades de parientes cercanos'
      }, {
        valor: 'Eventos académicos puntuales',
        nombre: 'Eventos académicos puntuales'
      }, {
        valor: 'Permisos personales',
        nombre: 'Permisos personales'
      }],
      motivosNoDescontables: [{
        valor: 'Misión oficial',
        nombre: 'Misión oficial'
      }, {
        valor: 'Seminarios',
        nombre: 'Seminarios'
      }, {
        valor: 'Otros, especifique',
        nombre: 'Otros, especifique'
      }],
      motivosReincorporacion: [{
        valor: 'Licencia con sueldo',
        nombre: 'Licencia con sueldo'
      }, {
        valor: 'Licencia sin sueldo',
        nombre: 'Licencia sin sueldo'
      }, {
        valor: 'Licencia especial',
        nombre: 'Licencia especial'
      }, {
        valor: 'Vacaciones',
        nombre: 'Vacaciones'
      }]
    };
  },
  async created() {
    try {
      // VALIDACIÓN: Solo llamar a Vuex si existe
      if (this.$store) {
        // Cargar historial desde Vuex
        if (this.fetchHistorialSolicitudes && (!this.historialSolicitudes || this.historialSolicitudes.length === 0)) {
          await this.fetchHistorialSolicitudes();
        }

        // Cargar tipos desde Vuex
        if (this.fetchTiposSolicitudes && (!this.tiposSolicitudes || this.tiposSolicitudes.length === 0)) {
          await this.fetchTiposSolicitudes();
        }
      } else {
        // Fallback: cargar datos directamente
        await this.fetchSolicitudes();
        await this.fetchTiposSolicitudesLocal();
      }
    } catch (error) {
      console.error('Error en created hook:', error);
      // Fallback final
      await this.fetchSolicitudes();
      await this.fetchTiposSolicitudesLocal();
    }
  },
  mounted() {
    this.$nextTick(() => {
      try {
        const editModalEl = document.getElementById('editSolicitudModal');
        const pdfModalEl = document.getElementById('pdfModal');
        if (editModalEl) {
          this.editModalInstance = new Modal(editModalEl);
        }
        if (pdfModalEl) {
          this.pdfModalInstance = new Modal(pdfModalEl);
        }
      } catch (error) {
        console.error('Error inicializando modales:', error);
      }
    });
  },
  beforeUnmount() {
    try {
      if (this.editModalInstance) {
        this.editModalInstance.dispose();
      }
      if (this.pdfModalInstance) {
        this.pdfModalInstance.dispose();
      }
    } catch (error) {
      console.error('Error disposing modales:', error);
    }
  },
  methods: {
    ...mapActions(['fetchHistorialSolicitudes', 'fetchTiposSolicitudes', 'refreshHistorialSolicitudes']),
    // Fallback methods - SIEMPRE disponibles
    async fetchSolicitudes() {
      try {
        const token = localStorage.getItem('auth_token');
        const response = await axios.get('http://localhost:8000/api/historial_solicitud', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        this.localSolicitudes = Array.isArray(response.data) ? response.data : [];
      } catch (error) {
        console.error('Error fetching solicitudes', error);
        this.localSolicitudes = []; // Asegurar que siempre sea un array
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: 'Error al cargar el historial de solicitudes'
        });
      }
    },
    async fetchTiposSolicitudesLocal() {
      try {
        const token = localStorage.getItem('auth_token');
        const response = await axios.get('http://localhost:8000/api/listar_solicitud', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        this.localTiposSolicitudes = Array.isArray(response.data) ? response.data : [];
      } catch (error) {
        console.error('Error fetching tipos de solicitudes', error);
        this.localTiposSolicitudes = []; // Asegurar que siempre sea un array
        if (error.response?.status !== 401 && error.response?.status !== 403) {
          // Solo mostrar error si no es de auth
        }
      }
    },
    async verPDF(solicitud) {
      this.isGeneratingPDF = solicitud.id_solicitud;
      this.solicitudViendoPDF = solicitud;
      this.pdfUrl = null;
      try {
        const token = localStorage.getItem('auth_token');
        const response = await axios.post('http://localhost:8000/api/obtener_ruta_pdf', {
          id_solicitud: solicitud.id_solicitud
        }, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        if (response.data.status && response.data.pdf_file_path) {
          this.pdfUrl = 'http://localhost:8000' + response.data.pdf_file_path;
          if (this.pdfModalInstance) {
            this.pdfModalInstance.show();
          }
        } else {
          throw new Error(response.data.message || 'No se pudo obtener el PDF');
        }
      } catch (error) {
        console.error('Error al obtener PDF:', error);
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: error.response?.data?.message || 'No se pudo obtener el PDF'
        });
      } finally {
        this.isGeneratingPDF = null;
      }
    },
    editarSolicitud(solicitud) {
      if (solicitud.estado !== 'pendiente') {
        Swal.fire({
          icon: 'warning',
          title: 'No se puede editar',
          text: 'Solo se pueden editar solicitudes en estado pendiente'
        });
        return;
      }
      this.solicitudEditando = {
        ...solicitud,
        tipo_solicitud_id: solicitud.tipo_solicitud.id_tipo_solicitud,
        fecha_inicio: this.formatDateForInput(solicitud.fecha_inicio),
        fecha_fin: this.formatDateForInput(solicitud.fecha_fin)
      };
      this.editErrors = {};
      if (this.editModalInstance) {
        this.editModalInstance.show();
      }
    },
    async guardarCambios() {
      if (!this.solicitudEditando) return;
      this.isUpdating = true;
      this.editErrors = {};
      try {
        const token = localStorage.getItem('auth_token');
        const datosActualizacion = {
          fecha_inicio: this.solicitudEditando.fecha_inicio,
          fecha_fin: this.solicitudEditando.fecha_fin,
          motivo: this.solicitudEditando.motivo || '',
          observacion: this.solicitudEditando.observacion || ''
        };
        if (this.esFormularioVacaciones) {
          datosActualizacion.salario = this.solicitudEditando.salario;
          datosActualizacion.dias = this.solicitudEditando.dias;
        } else if (this.esFormularioHorasExtras) {
          datosActualizacion.trabajo_realizado = this.solicitudEditando.trabajo_realizado;
          datosActualizacion.justificacion = this.solicitudEditando.justificacion;
        }
        const response = await axios.put(`http://localhost:8000/api/actualizar_solicitud/${this.solicitudEditando.id_solicitud}`, datosActualizacion, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        if (response.data && response.data.status === true) {
          if (this.editModalInstance) {
            this.editModalInstance.hide();
          }
          this.solicitudEditando = null;
          Swal.fire({
            icon: 'success',
            title: 'Solicitud actualizada',
            text: 'Los cambios se han guardado correctamente',
            timer: 2000,
            showConfirmButton: false
          });

          // Refrescar datos en Vuex o local
          if (this.refreshHistorialSolicitudes) {
            await this.refreshHistorialSolicitudes();
          } else {
            await this.fetchSolicitudes();
          }
        } else {
          throw new Error(response.data?.message || 'Error al actualizar la solicitud');
        }
      } catch (error) {
        console.error('Error al actualizar solicitud:', error);
        if (error.response?.status === 422) {
          this.editErrors = error.response.data.errors || {};
        }
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: error.response?.data?.message || 'Error al actualizar la solicitud'
        });
      } finally {
        this.isUpdating = false;
      }
    },
    getFullPdfUrl(relativePath) {
      if (!relativePath) return '';
      if (relativePath.startsWith('http')) {
        return relativePath;
      }
      return `${window.location.origin}${relativePath}`;
    },
    getBadgeClass(estado) {
      const classes = {
        'pendiente': 'bg-warning text-dark',
        'revision': 'bg-info text-white',
        'aprobada': 'bg-success text-white',
        'rechazada': 'bg-danger text-white'
      };
      return classes[estado] || 'bg-secondary text-white';
    },
    getEstadoText(estado) {
      const textos = {
        'pendiente': 'Pendiente',
        'revision': 'En Revisión',
        'aprobada': 'Aprobada',
        'rechazada': 'Rechazada'
      };
      return textos[estado] || estado;
    },
    formatFecha(fecha) {
      if (!fecha) return '';
      return moment(fecha).format('MMMM D YYYY, h:mm a');
    },
    formatDateForInput(fecha) {
      if (!fecha) return '';
      return moment(fecha).format('YYYY-MM-DD');
    }
  }
};
