import { createStore } from 'vuex'

export default createStore({
  state: {
    isAuthenticated: !!localStorage.getItem('auth_token')
  },
  getters: {
  },
  mutations: {
    setAuthentication(state, status) {
      state.isAuthenticated = status;
    }
  },
  actions: {
    login({ commit }) {
      commit('setAuthentication', true);
    },

  },
  modules: {
  }
})
