import { createApp } from 'vue';
import App from './App.vue';
import router from './router';
import store from './store';
import '@fortawesome/fontawesome-free/css/all.min.css';
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import moment from 'moment';
import axios from 'axios';

/* import the fontawesome core */
import { library } from '@fortawesome/fontawesome-svg-core';

/* import font awesome icon component */
import { FontAwesomeIcon } from '@fortawesome/vue-fontawesome';

/* import specific icons */
import { faCheck, faTimes, faEye } from '@fortawesome/free-solid-svg-icons';
import { faFaceGrinWide } from '@fortawesome/free-regular-svg-icons';

/* add icons to the library */
library.add(faCheck, faTimes, faEye, faFaceGrinWide);

// Configurar el interceptor de solicitudes
axios.interceptors.request.use(config => {
    const token = localStorage.getItem('auth_token');
    if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;
    }
    return config;
}, error => {
    return Promise.reject(error);
});

const app = createApp(App);
app.use(store);
app.use(router);

// Agregar axios al contexto global
app.config.globalProperties.$axios = axios;

// Agregar SweetAlert2 al contexto global
app.config.globalProperties.$swal = Swal;

// Agregar moment al contexto global
app.config.globalProperties.$moment = moment;

/* register font awesome icon component */
app.component('font-awesome-icon', FontAwesomeIcon);

app.mount('#app');
