import "core-js/modules/es.array.push.js";
import axios from 'axios';
import Swal from 'sweetalert2';
export default {
  name: 'Roles',
  data() {
    return {
      roles: [],
      users: [],
      currentPage: 1,
      rolesPerPage: 6,
      showModal: false,
      showAssignModal: false,
      showDeleteModal: false,
      isUsersModalVisible: false,
      newRole: '',
      userSearchQuery: '',
      selectedUser: null,
      selectedRole: null,
      selectedRoleToDelete: '',
      loading: true
    };
  },
  computed: {
    totalPages() {
      return Math.ceil(this.roles.length / this.rolesPerPage);
    },
    paginatedRoles() {
      const start = (this.currentPage - 1) * this.rolesPerPage;
      const end = start + this.rolesPerPage;
      return this.roles.slice(start, end);
    },
    filteredUsers() {
      if (!this.userSearchQuery) {
        return [];
      }
      const query = this.userSearchQuery.toLowerCase();
      return this.users.filter(user => user.correo_electronico.toLowerCase().includes(query)).slice(0, 5); // Limitar a 5 resultados
    },
    totalUsersCount() {
      return this.roles.reduce((sum, role) => sum + role.usuarios_count, 0) || 1;
    },
    visiblePages() {
      const delta = 2;
      const range = [];
      const rangeWithDots = [];
      let l;
      for (let i = 1; i <= this.totalPages; i++) {
        if (i === 1 || i === this.totalPages || i >= this.currentPage - delta && i <= this.currentPage + delta) {
          range.push(i);
        }
      }
      range.forEach(i => {
        if (l) {
          if (i - l === 2) {
            rangeWithDots.push(l + 1);
          } else if (i - l !== 1) {
            rangeWithDots.push('...');
          }
        }
        rangeWithDots.push(i);
        l = i;
      });
      return rangeWithDots;
    },
    deletableRoles() {
      return this.roles.filter(role => role.usuarios_count === 0);
    }
  },
  methods: {
    async listar_roles() {
      try {
        this.loading = true;
        const rolesResponse = await axios.get('http://localhost:8000/api/roles');
        const usersResponse = await axios.get('http://localhost:8000/api/listar_usuarios');
        this.roles = rolesResponse.data;
        this.users = Array.isArray(usersResponse.data.data) ? usersResponse.data.data : [];

        // Emitir estadísticas al componente padre
        this.$emit('update-stats', {
          totalRoles: this.roles.length,
          totalUsers: this.users.length
        });
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        this.loading = false;
      }
    },
    changePage(page) {
      if (page !== '...' && page >= 1 && page <= this.totalPages) {
        this.currentPage = page;
      }
    },
    nextPage() {
      if (this.currentPage < this.totalPages) {
        this.currentPage++;
      }
    },
    previousPage() {
      if (this.currentPage > 1) {
        this.currentPage--;
      }
    },
    getIcon(roleName) {
      const lowerRoleName = roleName.toLowerCase();
      if (lowerRoleName.includes('jefe') || lowerRoleName.includes('director') || lowerRoleName.includes('subdirector')) {
        return ['fas', 'user-tie'];
      } else if (lowerRoleName.includes('administrador')) {
        return ['fas', 'user-secret'];
      } else if (lowerRoleName.includes('analista')) {
        return ['fas', 'user-graduate'];
      } else if (lowerRoleName.includes('aseso')) {
        return ['fas', 'user-shield'];
      } else {
        return ['fas', 'user'];
      }
    },
    getColorForRole(roleName) {
      const lowerRoleName = roleName.toLowerCase();
      if (lowerRoleName.includes('administrador')) return '#dc3545';
      if (lowerRoleName.includes('jefe') || lowerRoleName.includes('director')) return '#007bff';
      if (lowerRoleName.includes('analista')) return '#28a745';
      if (lowerRoleName.includes('aseso')) return '#6f42c1';
      return '#6c757d';
    },
    showAddRoleModal() {
      this.showModal = true;
    },
    hideAddRoleModal() {
      this.showModal = false;
      this.newRole = '';
    },
    async createRole() {
      if (!this.newRole.trim()) return;
      try {
        const response = await axios.post('http://localhost:8000/api/agregar_roles', {
          name: this.newRole
        });
        this.roles.push(response.data.data);
        this.hideAddRoleModal();
        this.newRole = '';
        Swal.fire({
          icon: 'success',
          title: 'Rol creado',
          text: 'El rol ha sido creado correctamente',
          timer: 1000,
          showConfirmButton: false
        });
        this.listar_roles(); // Refrescar la lista de roles
      } catch (error) {
        console.error('Error creating role:', error);
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: 'Hubo un error al crear el rol',
          timer: 2000,
          showConfirmButton: false
        });
      }
    },
    showAssignUserModal(role) {
      this.selectedRole = role;
      this.showAssignModal = true;
    },
    hideAssignUserModal() {
      this.showAssignModal = false;
      this.selectedUser = null;
      this.userSearchQuery = '';
    },
    updateSearchResults() {
      this.selectedUser = null; // Limpiar usuario seleccionado si se cambia la búsqueda
    },
    selectUser(user) {
      this.selectedUser = user;
      this.userSearchQuery = user.correo_electronico; // Mostrar solo el correo electrónico en el campo de búsqueda
    },
    clearSelectedUser() {
      this.selectedUser = null;
      this.userSearchQuery = '';
    },
    async confirmAssignRoleToUser() {
      if (!this.selectedUser) {
        console.error('No user selected');
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: 'No se ha seleccionado ningún usuario',
          timer: 2000,
          showConfirmButton: false
        });
        return;
      }
      if (this.selectedRole.usuarios.some(user => user.correo_electronico === this.selectedUser.correo_electronico)) {
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: 'El usuario ya está asignado a este rol',
          timer: 2000,
          showConfirmButton: false
        });
        return;
      }
      this.assignRoleToUser();
    },
    async assignRoleToUser() {
      try {
        await axios.post('http://localhost:8000/api/asignar_rol', {
          user_id: this.selectedUser.id,
          role: this.selectedRole.name
        });
        this.hideAssignUserModal();
        this.selectedUser = null;
        this.userSearchQuery = '';
        Swal.fire({
          icon: 'success',
          title: 'Rol asignado',
          text: 'El rol ha sido asignado correctamente',
          timer: 1500,
          showConfirmButton: false
        });
        this.listar_roles();
        location.reload(); // Refrescar la lista de roles
      } catch (error) {
        if (error.response && error.response.status === 422) {
          Swal.fire({
            icon: 'error',
            title: 'Error',
            text: 'No se ha seleccionado ningún usuario',
            timer: 2000,
            showConfirmButton: false
          });
        } else {
          console.error('Error assigning role to user:', error);
          Swal.fire({
            icon: 'error',
            title: 'Error',
            text: 'Hubo un error al asignar el rol',
            timer: 2000,
            showConfirmButton: false
          });
        }
      }
    },
    showUsersModal(role) {
      this.selectedRole = role;
      this.isUsersModalVisible = true;
    },
    hideUsersModal() {
      this.isUsersModalVisible = false;
    },
    async desasignarRol(userId, roleName) {
      try {
        await axios.put('http://localhost:8000/api/desasignar_rol', {
          user_id: userId,
          role: roleName
        });
        Swal.fire({
          icon: 'success',
          title: 'Usuario desasignado',
          text: 'El usuario ha sido desasignado del rol correctamente',
          timer: 1500,
          showConfirmButton: false
        });
        this.hideUsersModal(); // Cerrar el modal después de desasignar
        this.listar_roles();
        location.reload(); // Refrescar la lista de roles
      } catch (error) {
        console.error('Error unassigning role from user:', error);
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: 'Hubo un error al desasignar el rol del usuario',
          timer: 2000,
          showConfirmButton: false
        });
      }
    },
    showDeleteRoleModal() {
      this.showDeleteModal = true;
    },
    hideDeleteRoleModal() {
      this.showDeleteModal = false;
      this.selectedRoleToDelete = '';
    },
    async confirmDeleteRole() {
      const roleToDelete = this.roles.find(role => role.name === this.selectedRoleToDelete);
      if (roleToDelete && roleToDelete.usuarios_count > 0) {
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: 'No se puede eliminar un rol con usuarios asignados',
          timer: 2000,
          showConfirmButton: false
        });
        return;
      }
      Swal.fire({
        title: '¿Estás seguro?',
        text: "¡No podrás revertir esto!",
        icon: 'warning',
        showCancelButton: true,
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33',
        confirmButtonText: 'Sí, eliminar',
        cancelButtonText: 'Cancelar'
      }).then(result => {
        if (result.isConfirmed) {
          this.deleteRole();
        }
      });
    },
    async deleteRole() {
      try {
        await axios.put(`http://localhost:8000/api/eliminar_rol/${this.selectedRoleToDelete}`);
        this.roles = this.roles.filter(role => role.name !== this.selectedRoleToDelete);
        this.hideDeleteRoleModal();
        this.selectedRoleToDelete = '';
        Swal.fire({
          icon: 'success',
          title: 'Rol eliminado',
          text: 'El rol ha sido eliminado correctamente',
          timer: 1000,
          showConfirmButton: false
        });
        this.listar_roles(); // Refrescar la lista de roles
      } catch (error) {
        console.error('Error deleting role:', error);
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: 'Hubo un error al eliminar el rol',
          timer: 2000,
          showConfirmButton: false
        });
      }
    }
  },
  mounted() {
    this.listar_roles();
  }
};
